import { createAsyncThunk } from '@reduxjs/toolkit';
 import { addContact, getContacts, removeContact } from '../Api/Api';
//import * as Api from '../Api/Api';


export const addContacts = createAsyncThunk('contacts/addContact', async (contact, thunkApi) => {
  try {
    const contacts = await addContact(contact);
    return contacts;
  } catch (error) {
    return thunkApi.rejectWithValue(error);
  }
});

export const getContact = createAsyncThunk('contacts/getContacts', async (_, thunkApi) => {
  try {
    const contacts = await getContacts();
    console.log('contacts :>> ', contacts);
    return contacts;
  } catch (error) {
    return thunkApi.rejectWithValue(error);
  }
});

export const removeContacts = createAsyncThunk('contacts/removeContact', async (id, thunkApi) => {
  try {
    const respId = await removeContact(id);
    return respId;
  } catch (error) {
    return thunkApi.rejectWithValue(error);
  }
});
