import { createAsyncThunk } from '@reduxjs/toolkit';
// import { addContactApi, getContactsApi, deleteContactApi } from '../Api/API';
import * as Api from '../Api/API';

export const addContact = createAsyncThunk('contacts/addContact', async (contact, thunkApi) => {
  try {
    const contacts = await addContactApi(contact);
    return contacts;
  } catch (error) {
    return thunkApi.rejectWithValue(error);
  }
});

export const getContacts = createAsyncThunk('contacts/getContacts', async (_, thunkApi) => {
  try {
    const contacts = await getContactsApi();
    return contacts;
  } catch (error) {
    return thunkApi.rejectWithValue(error);
  }
});

export const deleteContact = createAsyncThunk('contacts/deleteContact', async (id, thunkApi) => {
  try {
    const respId = await deleteContactApi(id);
    return respId;
  } catch (error) {
    return thunkApi.rejectWithValue(error);
  }
});
